<?php
// deposit.php - This script fetches a specific crypto asset's details based on the URL parameter
// and populates the deposit page dynamically.

// Start the session to access session variables.
// Ensure this is called only once at the top of your scripts.
if (session_status() === PHP_SESSION_NONE) {
    session_start();
}

// Check if the user is logged in. If not, redirect to the login page.
if (!isset($_SESSION['user_id'])) {
    header("Location: login.php");
    exit;
}

// 1. Establish a database connection
// Assuming 'config/db.php' handles the database connection and returns a $conn object.
require_once 'db.php';
if ($conn->connect_error) {
    die("Connection failed: " . $conn->connect_error);
}

// 2. Get the asset ID from the URL (e.g., deposit.php?id=1)
$asset_id = isset($_GET['id']) ? intval($_GET['id']) : 0;

// If the asset ID is not valid, show an error message
if ($asset_id === 0) {
    die("Invalid asset ID!");
}

// 3. Prepare and execute the SQL query to fetch the asset data
// We are now also selecting the balance to be consistent with the withdraw page.
// Use a prepared statement to prevent SQL injection.
$sql = "SELECT `id`, `user_id`, `symbol`, `name`, `balance` FROM `user_assets` WHERE `id` = ?";
$stmt = $conn->prepare($sql);
$stmt->bind_param("i", $_SESSION['user_id']);
$stmt->execute();
$result = $stmt->get_result();
$asset = $result->fetch_assoc();

// If no asset is found, display an error
if (!$asset) {
    die("Asset not found or you are not authorized to access it!");
}

// Close the statement and connection
$stmt->close();
$conn->close();

// In a real application, this would be a real, generated deposit address.
// For this example, we'll use a hardcoded placeholder address.
$deposit_address = "bc1qxy2kgdygjrsqtzq2n0yrf24a0r8j3j3j8r2y"; // Example BTC address

